function loadAudio(){getFileName(sura,ayat),link=base_link+file_name,audio.src=link,audio.load()}function playPause(){playing?(audio.pause(),playing=!1):(ini&&sura>1?(ini=!1,bismillah.src=base_link+"001001.ogg",bismillah.load(),bismillah.play(),bismillah.addEventListener("ended",function(){playAudio()})):playAudio(),playing=!0)}function playAudio(){loadAudio(),highLight(),playing=!0,audio.play()}function stopAudio(){finished(),audio.pause(),playing=!1,ini=!0,ayat=1,link=""}function playNext(){ayat<verses&&playing&&(finished(),audio.pause(),ayat+=1,playAudio())}function playPrev(){ayat>1&&playing&&(finished(),audio.pause(),ayat-=1,playAudio())}function highLight(){var a="a"+ayat,i=document.getElementById(a);i.className="a_ayat",i.scrollIntoView(),document.getElementById("loader").style.width=ayat/verses*100+"%"}function finished(){var a="a"+ayat;document.getElementById(a).className="ayat",document.getElementById("loader").style.width="0%"}function getFileName(a,i){var e="",n="";e=a<10?"00"+a:a<100&&a>9?"0"+a:a,n=i<10?"00"+i:i<100&&i>9?"0"+i:i,file_name=e+n+".ogg"}var audio=new Audio,bismillah=new Audio,ayat=1,verses=0,link="",playing=!1,ini=!0,base_link="https://download.quranicaudio.com/verses/Alafasy/ogg/",file_name="";audio.addEventListener("ended",function(){finished(),ayat<verses&&playNext()});